import React, { createContext, useState, useEffect } from "react";
import { register, login, isTokenValid } from "../axios/auth";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const checkToken = async () => {
      const token = localStorage.getItem("profilesAuthToken");
      if (token) {
        try {
          const isValid = await isTokenValid(); // Validate the token on the server
          if (isValid) {
            setIsLoggedIn(true);
            setUserData(isValid.user); // Assuming the server returns user data
          } else {
            handleLogout(); // Logout if token is invalid
          }
        } catch (error) {
          console.error("Token validation failed:", error.message);
          handleLogout();
        }
      }
    };

    checkToken();
  }, []);

  const authContextRegister = async (user) => {
    setIsLoggedIn(false);
    try {
      await register(user);
      console.log("Registered successfully!");
    } catch (error) {
      console.error("Registration error:", error.message);
      setIsLoggedIn(false);
      throw error;
    }
  };

  const authContextLogin = async (user) => {
    setIsLoggedIn(false);
    try {
      const res = await login(user);
      console.log("Logged in successfully!");
      setIsLoggedIn(true);
      setUserData(res.user);
      console.log(user);
      
    } catch (error) {
      console.error("Login error:", error.message);
      setIsLoggedIn(false);
      throw error;
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("profilesAuthToken");
    setIsLoggedIn(false);
    setUserData(null);
  };

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn,
        authContextLogin,
        authContextRegister,
        authContextLogout: handleLogout,
        userData,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
