import React, { createContext, useState, useEffect } from "react";
import { register, login } from "../axios/auth";
import { API } from "../axios/auth";
import {jwtDecode} from "jwt-decode";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkTokenValidity = async () => {
      const token = localStorage.getItem("profilesAuthToken");
      console.log('no Token User');

      if (token) {
        try {
          const decodedToken = jwtDecode(token);
          const currentTime = Date.now() / 1000;

          if (decodedToken.exp < currentTime) {
            console.error("Token expired, logging out.");
            authContextLogout();
          } else {
            const response = await API.post("/protected", {user: {...userData}}); // Verify token with the server
            setIsLoggedIn(true);
            setUserData({
              fullName: response.data.user.fullName,
              nickName: response.data.user.nickName,
            });
          }
          console.log('user Token OK');
          
        } catch (error) {
          console.log(error);
          console.error("Invalid token, logging out.");
          authContextLogout();
        }
      }
      setIsLoading(false); // Mark loading as complete
    };

    checkTokenValidity();
  }, [userData]);

  const authContextRegister = async (user) => {
    setIsLoading(true);
    try {
      const res = await register(user);
      setIsLoading(false);
      console.log("Registered successfully!");
      return res;
    } catch (error) {
      setIsLoading(false);
      throw error;
    }
  };

  const authContextLogin = async (user) => {
    setIsLoading(true);
    setIsLoggedIn(false);
    try {
      const response = await login(user);
      localStorage.setItem("profilesAuthToken", response.token);
      setUserData({
        fullName: response.user.fullName,
        nickName: response.user.nickName,
        _id: response.user._id,
      });
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      setIsLoading(false);
      setIsLoading(false);
      throw error;
    }
  };

  const authContextLogout = () => {
    localStorage.removeItem("profilesAuthToken");
    localStorage.removeItem("profilesUserData");
    setUserData(null);
    setIsLoading(false);
    setIsLoading(false);
  };

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn,
        authContextLogin,
        authContextRegister,
        authContextLogout,
        userData,
        isLoading
      }}
    >
     {children}
    </AuthContext.Provider>
  );
};
