import React, { createContext, useState, useEffect } from "react";
import { register, login, isTokenValid } from "../axios/auth";
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState({});

  useEffect(() => {
    // Check localStorage for the token on app load
    const token = localStorage.getItem("profilesAuthToken");
    if (token) {
      isTokenValid()
    }
    setIsLoggedIn(!!token); // Set logged in if token exists
  }, []);

  const authContextRegister = async (user) => {
    setIsLoggedIn(false);
    try {
      await register(user);
      console.log("rigisterd!!!");
    } catch (error) {
      setIsLoggedIn(false);
      throw error;
    }
  };

  const authContextLogin = async (user) => {
    setIsLoggedIn(false);
    try {
      const res = await login(user);
      console.log("login!!!");
      setIsLoggedIn(true);
      setUserData(res.user);
    } catch (error) {
      setIsLoggedIn(false);
      throw error;
    }
  };

  const authContextLogout = () => {
    localStorage.removeItem("profilesAuthToken");
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn,
        authContextLogin,
        authContextRegister,
        authContextLogout,
        userData
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
