import { useState } from "react";
import classes from "./signUp.module.css";

const SignUp = () => {
  const [signUpFormState, setSignUpFormState] = useState({
    fullName: {
      value: "",
      type: "text",
      name: "Full Name",
      valid: {
        min: 3,
        max: 20,
      },
      error: "",
    },
    nickName: {
      value: "",
      type: "text",
      name: "Nick Name",
      valid: {
        min: 3,
        max: 20,
      },
      error: "",
    },
    password: {
      value: "",
      type: "password",
      name: "Password",
      valid: {
        min: 6,
        max: 20,
      },
      error: "",
    },
  });

  const inputhHandler = (val, key) => {
    setSignUpFormState((prev) => ({
      ...prev,
      [key]: {
        ...prev[key],
        value: val,
      },
    }));
    if (
      val.length < signUpFormState[key].valid.min ||
      val.length > signUpFormState[key].valid.max
    ) {
        setSignUpFormState((prev) => ({
            ...prev,
            [key]: {
              ...prev[key],
            error: `${key} length must be ${signUpFormState[key].valid.min} - ${signUpFormState[key].valid.max}`,
            },
          }));
    }else{
        setSignUpFormState((prev) => ({
            ...prev,
            [key]: {
              ...prev[key],
            error: ``,
            },
          }));
    }
  };

  const isVformValid = () => {
    let valid = false;
    Object.entries(signUpFormState).forEach(([key,val]) => {
        if (val.error === ""){
            valid = false;
            return valid;
        }else{
            valid=true;
        };
    });
    return valid;
  };

  let signUpForm = (
    <form className={classes.signUpForm}>
      <h1>SIGN UP</h1>
      {Object.entries(signUpFormState).map(([key, value]) => {
        return (
          <div key={key} className={classes.signUpFormInputDiv}>
            <label>
              {value.name}
              <input
                minLength={value.valid.min}
                maxLength={value.valid.max}
                type={value.type}
                value={value.value}
                onChange={(e) => inputhHandler(e.target.value, key)}
              />
            </label>
            {value.error !== "" && <p className={classes.signUpFormInputError}>{value.error}</p>}
          </div>
        );
      })}
      <button disabled={isVformValid()}></button>
    </form>
  );

  return <div>{signUpForm}</div>;
};

export default SignUp;
