import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check localStorage for the token on app load
    const token = localStorage.getItem('profilesAuthToken');
    setIsLoggedIn(!!token); // Set logged in if token exists
  }, []);

  const authContextLogin = (token) => {
    localStorage.setItem('profilesAuthToken', token);
    setIsLoggedIn(true);
  };

  const authContextLogout = () => {
    localStorage.removeItem('profilesAuthToken');
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, authContextLogin, authContextLogout }}>
      {children}
    </AuthContext.Provider>
  );
};
