import React, { createContext, useState, useEffect } from "react";
import { register, login } from "../axios/auth";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Check localStorage for the token on app load
    const token = localStorage.getItem("profilesAuthToken");
    const userDataFromStorage = localStorage.getItem("profilesUserData");
    setIsLoggedIn(!!token); // Set logged in if token exists
    if(token && userDataFromStorage) {
      setIsLoggedIn(!!token);
      setUserData(userDataFromStorage);
    }else{
      authContextLogout();
    }
  }, []);

  const authContextRegister = async (user) => {
    setIsLoggedIn(false);
    try {
      await register(user);
      console.log("rigisterd!!!");
    } catch (error) {
      setIsLoggedIn(false);
      throw error;
    }
  };

  const authContextLogin = async (user) => {
    setIsLoggedIn(false);
    try {
      const res = await login(user);
      console.log("login!!!");
      setIsLoggedIn(true);
      localStorage.setItem("profilesUserData", {
        fullNmae: res.user.fullNmae,
        nickName: res.user.nickname,
      });
      setUserData({ fullNmae: res.user.fullNmae, nickName: res.user.nickname });
    } catch (error) {
      setIsLoggedIn(false);
      throw error;
    }
  };

  const authContextLogout = () => {
    localStorage.removeItem("profilesAuthToken");
    localStorage.removeItem("profilesUserData");
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn,
        authContextLogin,
        authContextRegister,
        authContextLogout,
        userData,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
