import { useState, useEffect } from "react";
import { isLoggedIn } from "../../../utilities/auth/isUserLoggedIn";
import classes from "./nav.module.css";

const Nav = () => {
  const [userState, setUserState] = useState(isLoggedIn());

  const handleTokenChange = () => {
    setUserState(isLoggedIn());
  };

  useEffect(() => {
    // Update on component mount
    handleTokenChange();

    // Listen for localStorage changes
    window.addEventListener("storage", handleTokenChange);

    // Cleanup listener
    return () => {
      window.removeEventListener("storage", handleTokenChange);
    };
  }, []);

  return (
    <div className={classes.nav}>
      <h1 className={classes.navTitle}>Profiles</h1>
      {userState && <button className={classes.logOutButton}>Log Out</button>}
    </div>
  );
};

export default Nav;
