import { useState } from "react";
import classes from "./signUp.module.css";

const SignUp = () => {
  const [formType, setFormType] = useState("signUp")
  const [signUpFormState, setSignUpFormState] = useState({
    fullName: {
      value: "",
      type: "text",
      name: "Full Name",
      valid: {
        min: 3,
        max: 20,
      },
      error: "",
    },
    nickName: {
      value: "",
      type: "text",
      name: "Nick Name",
      valid: {
        min: 3,
        max: 20,
      },
      error: "",
    },
    password: {
      value: "",
      type: "password",
      name: "Password",
      valid: {
        min: 6,
        max: 20,
      },
      error: "",
    },
  });

  // Handle input change and validation
  const inputHandler = (val, key) => {
    const { min, max } = signUpFormState[key].valid;

    const error =
      val.length < min || val.length > max
        ? `${signUpFormState[key].name} length must be between ${min} and ${max} characters.`
        : "";

    setSignUpFormState((prev) => ({
      ...prev,
      [key]: {
        ...prev[key],
        value: val,
        error: error,
      },
    }));
  };

  // Check if the form is valid
  const isFormValid = () => {
    return Object.values(signUpFormState).every((field) => field.error === "" && field.value.trim() !== "");
  };

  let signUpForm = (
    <form className={classes.signUpForm}>
      <h1>SIGN UP</h1>
      {Object.entries(signUpFormState).map(([key, value]) => {
        return (
          <div key={key} className={classes.signUpFormInputDiv}>
            <label>
              {value.name}
              <input
                className={value.error !== "" ? classes.invalidInput : classes.input}
                minLength={value.valid.min}
                maxLength={value.valid.max}
                type={value.type}
                value={value.value}
                onChange={(e) => inputHandler(e.target.value, key)}
              />
            </label>
            {value.error && <p className={classes.signUpFormInputError}>{value.error}</p>}
          </div>
        );
      })}
      <p> {formType === "signUp" ? "Already have account? Login here" : "Dont have account yet? Sign Up here"}</p>
      <button disabled={!isFormValid()} className={classes.signUpFormButton}>
        Sign Up
      </button>
    </form>
  );

  return <div>{signUpForm}</div>;
};

export default SignUp;
