import React, { createContext, useState, useEffect } from 'react';
import { register, login } from "../axios/auth";


export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  

  useEffect(() => {
    // Check localStorage for the token on app load
    const token = localStorage.getItem('profilesAuthToken');
    setIsLoggedIn(!!token); // Set logged in if token exists
  }, []);

  const authContextRegister = async (user) => {
    setIsLoggedIn(false);
    try {
      await register(user);
      console.log("rigisterd!!!");
      setIsLoggedIn(true);
    } catch (error) {
      setIsLoggedIn(false);
     return error.message;
    }
  };

  const authContextLogin = async (user) => {
    setIsLoggedIn(false);
    try {
      await login(user);
      console.log("login!!!");
      setIsLoggedIn(true);
    } catch (error) {
      setIsLoggedIn(false);
      return error.message;
    }
  };

  const authContextLogout = () => {
    localStorage.removeItem('profilesAuthToken');
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, authContextLogin,authContextRegister, authContextLogout }}>
      {children}
    </AuthContext.Provider>
  );
};
